mapper.insert:
INSERT INTO board
(
	boardnum
	, userid
	, title
	, text
	
	, originalfile
	, savedfile
	
)
VALUES
(
	board_seq.nextval
	, #{userid}
	, #{title}
	, #{text}
	
	, #{originalfile}
	, #{savedfile}
	
)

mapper.select:
SELECT * FROM ( SELECT rownum n, b.* FROM ( SELECT boardnum , userid , title , text , to_char(regdate, 'YYYY-MM-DD') as regdate , hitcount , originalfile , savedfile FROM board WHERE
title userid text
LIKE '%' || #{searchWord} ||'%' ORDER BY boardnum DESC ) b ) WHERE n >= #{srow} and n <= #{erow}

mapper.update:
UPDATE board
SET 
	title = #{title}
	, text = #{text}
	, regdate = sysdate
	
	
	, originalfile = #{originalfile}
	, savedfile = #{savedfile}
	
	
	
	, originalfile = ''
	, savedfile = ''
	
	
	
WHERE 
	boardnum = #{boardnum}
